import theChi from "../images/posters/landscape/the.chi.png";
import theChi2 from "../images/posters/portrait/the.chi.jpg";
import React, { useState, useEffect } from "react";

function MainSlider() {
  const [activeSlide, setActiveSlide] = useState(1);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveSlide((prevSlide) => (prevSlide % 3) + 1);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <div className="w-full md:w-2/3 bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700 relative">
        <a href="#">
          <img
            className="rounded-t-lg object-cover"
            src={theChi}
            alt="The Chi"
          />
        </a>
        <div className="z-10 absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-gray-900 via-gray-800 to-transparent text-white rounded-lg">
          <a href="#">
            <h5 className="text-5xl font-semibold tracking-tight hover:text-blue-600">
              The Chi
            </h5>
          </a>
          <div className="flex w-full">
            <span className="text-sm flex-grow">S06E05</span>
            <div className="flex float-right p-0.5 rounded align-middle">
              <svg
                class="w-4 h-4 text-yellow-300"
                aria-hidden="true"
                xmlns="http://www.w3.org/2000/svg"
                fill="currentColor"
                viewBox="0 0 22 20"
              >
                <path d="M20.924 7.625a1.523 1.523 0 0 0-1.238-1.044l-5.051-.734-2.259-4.577a1.534 1.534 0 0 0-2.752 0L7.365 5.847l-5.051.734A1.535 1.535 0 0 0 1.463 9.2l3.656 3.563-.863 5.031a1.532 1.532 0 0 0 2.226 1.616L11 17.033l4.518 2.375a1.534 1.534 0 0 0 2.226-1.617l-.863-5.03L20.537 9.2a1.523 1.523 0 0 0 .387-1.575Z" />
              </svg>
              <p>5.0</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MainSlider;
