import theChi from "../images/posters/landscape/the.chi.png";
import theChi2 from "../images/posters/portrait/the.chi.jpg";
import React, { useState, useEffect } from "react";

function MainSlider() {
  const [activeSlide, setActiveSlide] = useState(1);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveSlide((prevSlide) => (prevSlide % 3) + 1);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <div className="carousel w-full h-72 rounded md:h-96">
        <div id="slide1" className="carousel-item relative w-full">
          <img
            src={theChi}
            className={`carousel-item relative w-full ${
              activeSlide === 1 ? "" : "hidden"
            }`}
            alt=""
          />
          <h1 className="absolute right-20 top-56 md:top-64 font-bold text-white text-xl">
            The Chi
          </h1>
          <div className="absolute flex justify-between transform -translate-y-1/2 left-5 right-5 top-1/2">
            <a href="#slide4" className="btn btn-circle">
              ❮
            </a>
            <a href="#slide2" className="btn btn-circle">
              ❯
            </a>
          </div>
        </div>
        <div id="slide2" className="carousel-item relative w-full">
          <img
            src={theChi2}
            className={`carousel-item relative w-full ${
              activeSlide === 2 ? "" : "hidden"
            }`}
            alt=""
          />
          <div className="absolute flex justify-between transform -translate-y-1/2 left-5 right-5 top-1/2">
            <a href="#slide1" className="btn btn-circle">
              ❮
            </a>
            <a href="#slide3" className="btn btn-circle">
              ❯
            </a>
          </div>
        </div>
        <div className="flex justify-center w-full md:w-2/3 py-2 gap-2 z-20 absolute top-96">
          <a href="#slide1" className="btn btn-xs btn-circle p-0 outline"></a>
          <a href="#slide2" className="btn btn-xs btn-circle p-0 outline"></a>
        </div>
      </div>
    </div>
  );
}

export default MainSlider;
