import React, { useEffect, useState } from "react";
import { connect, useSelector } from "react-redux";
import googleLogo from "../images/google.png";
import { signin } from "../actions/auth";
import { useNavigate } from "react-router-dom";

const Signin = ({ signin, isAuthenticated }) => {
  const [loading, setLoading] = useState(false);
  const signin_error = useSelector((state) => state.auth.error);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      signin(email, password);
    } catch (error) {
    } finally {
      setLoading(false);
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    if (isAuthenticated) {
      navigate("/", { replace: true });
    }
  }, [isAuthenticated, navigate]);

  return (
    <dialog id="sign_in" className="modal">
      <form
        method="dialog"
        className="modal-box space-y-6"
        onSubmit={(e) => onSubmit(e)}
      >
        <div className="flex flex-col mb-2">
          <div className="flex p-0">
            <a
              href
              className="btn btn-md rounded-none btn-ghost normal-case text-xl font-bold text-center flex-1 hover:bg-white"
            >
              Sign in
            </a>
            <a
              href
              onClick={() => window.sign_up.showModal()}
              className="btn btn-md btn-ghost normal-case text-xl font-bold text-center flex-1 text-rose-500 hover:bg-white"
            >
              Sign up
            </a>
          </div>
          <progress className="progress w-1/2 h-1" value={50} />
        </div>
        <div>
          <label
            for="email"
            className="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Email
          </label>
          <input
            required
            type="email"
            name="email"
            value={email}
            autoComplete="off"
            onChange={(e) => onChange(e)}
            id="email"
            className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
            placeholder="name@gmail.com"
          />
        </div>
        <div>
          <label
            for="password"
            className="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Password
          </label>
          <input
            required
            type="password"
            name="password"
            value={password}
            onChange={(e) => onChange(e)}
            minLength={6}
            id="password"
            placeholder="••••••••"
            className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
          />
        </div>
        {signin_error && (
          <label className="label-text-alt text-red-500">{signin_error}</label>
        )}
        <div className="flex items-start">
          <div className="flex items-start">
            <div className="flex items-center h-5">
              <input
                id="remember"
                type="checkbox"
                value=""
                className="w-4 h-4 border border-gray-300 rounded bg-gray-50 focus:ring-3 focus:ring-blue-300 dark:bg-gray-700 dark:border-gray-600 dark:focus:ring-blue-600 dark:ring-offset-gray-800 dark:focus:ring-offset-gray-800"
              />
            </div>
            <label
              for="remember"
              className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300"
            >
              Remember me
            </label>
          </div>
          <a
            href="/forgot-password"
            className="ml-auto text-sm text-blue-700 hover:underline dark:text-blue-500"
          >
            Forgot Password?
          </a>
        </div>
        <button
          href
          type="submit"
          className="btn w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
        >
          {loading ? "Signing In..." : "Sign In"}
        </button>
        <div className="divider">or continue with</div>
        <div className="flex mt-3 w-full">
          <div className="m-auto space-x-2">
            <button className="btn btn-square btn-outline p-1">
              <img src={googleLogo} alt="google" width={20} height={20} />
            </button>
            <button className="btn btn-square btn-outline p-1">
              <img src={googleLogo} alt="google" width={20} height={20} />
            </button>
          </div>
        </div>
      </form>
      <form method="dialog" className="modal-backdrop">
        <button>close</button>
      </form>
    </dialog>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { signin })(Signin);
