import React, { useState } from "react";
import { redirect } from "react-router-dom";
import { verify } from "../actions/auth";
import { connect } from "react-redux";

function Activate({ verify, match }) {
  const [verified, setVerified] = useState(false);

  const verifyAccount = (e) => {
    const uid = match.params.uid;
    const token = match.params.token;

    verify(uid, token);
    setVerified(true);
  };

  if (verified) {
    redirect("/");
  }

  return (
    <div className="flex flex-col justify-center w-auto p-4 ml-auto mr-auto">
      <h1 className="normal-case text-2xl font-bold text-center ">
        Verify Your Account
      </h1>
      <button
        onClick={verifyAccount}
        className="btn btn-outline btn-sm md:btn-md rounded-full"
      >
        Verify
      </button>
    </div>
  );
}

export default connect(null, { verify })(Activate);
