import React, { useState } from "react";
import { redirect } from "react-router-dom";
import { verify } from "../actions/auth";
import { connect } from "react-redux";

function Activate({ verify, match }) {
  const [verified, setVerified] = useState(false);

  const verifyAccount = (e) => {
    const uid = match.params.uid;
    const token = match.params.token;

    verify(uid, token);
    setVerified(true);
  };

  if (verified) {
    redirect("/");
  }
  return (
    <div>
      <div className="relative flex flex-col justify-center overflow-hidden p-4 mt-20">
        <h1 className="normal-case text-2xl font-bold text-center ">
          Verify Your Account
        </h1>
        <div>
          <a
            href
            onClick={verifyAccount}
            className="btn btn-md rounded-full normal-case text-2xl font-bold text-center hover:bg-white mt-20"
          >
            Verify
          </a>
        </div>
      </div>
    </div>
  );
}

export default connect(null, { verify })(Activate);
